import React from 'react';
import Login from './Login';
import { useState,useEffect } from 'react';

function NavBar() {
  const [showPopup, setShowPopup] = useState(false);
  const [loggedIn, setLoggedIn] = useState(false);

  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  useEffect(() => {
    // Check if user data exists in local storage
    const userData = localStorage.getItem('user');

    if (userData) {
      setLoggedIn(userData);
    }
  }, []);

  const [showSidebar, setShowSidebar] = useState(false);

  const togglePopup2 = () => {
    // If the user is logged in, toggle the sidebar
    if (loggedIn) {
      setShowSidebar(!showSidebar);
    } else {
      // Handle sign-in logic if the user is not logged in
      // You can redirect to a sign-in page or show a sign-in modal
    }
  };


  const closePopup = () => {
    setShowPopup(false); // Set showPopup to false to close the popup
  };
  return (
    <div className='container-fluid mx-auto'>
      <div className='flex justify-between items-center pl-10 pr-10'>
        <div>
          <img
            className='h-16 sm:h-24'
            src='https://getlogo.net/wp-content/uploads/2020/04/bookmyshow-logo-vector.png'
            alt='logo'
          />
        </div>
        <div className='hidden sm:block sm:flex-grow mx-3'>
          <input
            type='search'
            className='form-control w-2/4 ml-8'
            placeholder='Search movies, events'
          />
        </div>
        <div className='sm:block font-arial'>
          <h3 className='text-right'>Location</h3>
        </div>
        <div>
      <button
        className='bg-[#950740] ml-4 h-8 w-40 rounded-lg text-white hover:cursor-pointer'
        onClick={togglePopup2}
      >
        {loggedIn ? 'Toggle Sidebar' : 'SignIn'}
      </button>

      {loggedIn && (
        // Render the sidebar only if the user is logged in
        <div
          id="sidebar"
          className={`w-48 d-absolute h-screen bg-gray-800 text-white fixed right-0 transition-transform transform ${
            showSidebar ? 'translate-x-0' : 'translate-x-full'
          }`}
        >
          {/* Your sidebar content goes here */}
          <button onClick={() => setShowSidebar(false)}>Close Sidebar</button>
          <p className="p-4">Sidebar Content</p>
        </div>
      )}


  {showPopup && (
    <div
      id="login-popup"
      tabIndex="-1"
      className="bg-black/50 overflow-y-auto overflow-x-hidden fixed top-0 right-0 left-0 z-50 h-full items-center justify-center flex"
    >
      <Login />
      {showPopup && <Login onClose={closePopup} />} 
    </div>
  )}
</div>

      </div>
      <div className='snap-mandatory snap-x flex justify-between font-serif h-14 pl-10 pr-10 hover:cursor-pointer' style={{ backgroundColor: 'rgb(245, 245, 245)' }}>
        <ul className='snap-center flex space-x-8 text-14px sm:text-lg'>
          <li>Movies</li>
          <li>Events</li>
          <li>Sports</li>
          <li>Streams</li>
        </ul>
        <ul className='snap-center flex space-x-8 text-14px sm:text-lg hover:cursor-pointer'>
          <li>Offers</li>
          <li>Gift Cards</li>
        </ul>
      </div>
    </div>
  );
}

export default NavBar;
